// smallest nsquare>12000
public class Smallest_n2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=0;
		while(Math.pow(0,2)>12000) {
			n++;
			if(n<12000) {
				System.out.println("the lowest smalest n2 sqare is "+n );
				break;
			}
			
		}
	}

}
